        public OrderController(DataContext context,IUserHelper userHelper, ICartRepository cartRepository, IOrderRepository orderRepository, IInfoClientRepository infoClientRepository, IShippingInfoRepository shippingInfoRepository, IPaymentMethodRepository paymentMethodRepository, IProductRepository productRepository, IMailHelper mailHelper)
        {
            _context = context;
            _userHelper = userHelper;
            _cartRepository = cartRepository;
            _orderRepository = orderRepository;
            _infoClientRepository = infoClientRepository;
            _shippingInfoRepository = shippingInfoRepository;
            _paymentMethodRepository = paymentMethodRepository;
            _productRepository = productRepository;
            _mailHelper = mailHelper;
        }
        [Authorize(Roles = "Client")]
        public async Task<IActionResult> Index()
        {
            
             var user = await _userHelper.GetUserByEmailAsync(this.User.Identity.Name);
            return View( _orderRepository.GetAll().Include(p=>p.User)
                    .Include(o => o.Items)
                    .ThenInclude(p => p.Product).Where(p=> p.User.Id == user.Id ).ToList());
        }
        
        [Authorize(Roles = "Client")]
        public async Task<IActionResult> Create(string shipping, decimal discount)
        {
            decimal shippingtaxes = 0.00M;
            int days = 0;
            if (shipping != null)
            {
                
                if (shipping == "CttExpress")
                {
                     shippingtaxes = 4.89M;
                     days = 1;
                }
                else
                {
                    shippingtaxes = 2.75M;
                    days = 7;
                }
            }
            var user = await _userHelper.GetUserByEmailAsync(this.User.Identity.Name);
            List<CartItem> cartItems = new List<CartItem>();
            OrderViewModel model = new OrderViewModel();
            cartItems = _cartRepository.GetAll().Where(p => p.UserId == user.Id).ToList();
            var infoClient =await _infoClientRepository.GetAll().Where(p => p.UserId == user.Id).FirstOrDefaultAsync();
            var infoshippingclient = await  _shippingInfoRepository.GetAll().Where(p => p.UserId == user.Id).FirstOrDefaultAsync();
            var infopaymentclient = await _paymentMethodRepository.GetAll().Where(p => p.UserId == user.Id).FirstOrDefaultAsync();
            if(infoClient == null )
            {
                model = new OrderViewModel
                {
                    CartItems = cartItems,
                    DeliveryDate = System.DateTime.Today.AddDays(days),
                    Email = user.Email,
                    ShippingMethod = shipping,
                    ShippingTaxes = shippingtaxes,
                    Discount = discount
                    
                    


                };
            }
            else
            {
                model = new OrderViewModel
                {
                    CartItems= cartItems,
                    FullName = infoshippingclient.RecepientName,
                    Address = infoshippingclient.Address,
                    District = infoshippingclient.District,
                    ZipCode = infoshippingclient.PostalCode,
                    NameOnCard = infopaymentclient.NameOnCard,
                    CardNumber = infopaymentclient.CardNumber,
                    ExpiryDate = infopaymentclient.ExperyDate,
                    Discount = discount,
                    ShippingMethod = shipping,
                    ShippingTaxes = shippingtaxes,
                    CVV = infopaymentclient.CVV,
                    Email = user.Email,
                   
                    
                    DeliveryDate = System.DateTime.Today.AddDays(days),
                    User = await _userHelper.GetUserByEmailAsync(this.User.Identity.Name),
                    UserId = user.Id
                };
            }
           

            return View(model);
        }
